-- drop objects
drop table d40_tipo cascade constraints;
drop table d40_prodotto cascade constraints;
drop table d40_cliente cascade constraints;
drop table d40_fornitore cascade constraints;
drop table d40_acquisto cascade constraints;
drop table d40_vendita cascade constraints;

-- create tables
create table d40_tipo (
    id                             number generated by default on null as identity 
                                   constraint d40_tipo_id_pk primary key,
    tipo                           varchar2(50 char)
                                   constraint d40_tipo_tipo_unq unique not null,
    gruppo                         varchar2(50 char) not null
)
;

create table d40_prodotto (
    id                             number generated by default on null as identity 
                                   constraint d40_prodotto_id_pk primary key,
    tipo_id                        number
                                   constraint d40_prodotto_tipo_id_fk
                                   references d40_tipo on delete cascade not null,
    prodotto                       varchar2(50 char)
                                   constraint d40_prodotto_prodotto_unq unique not null,
    prezzo_acquisto                number not null,
    prezzo_vendita                 number not null
)
;

-- table index
create index d40_prodotto_i1 on d40_prodotto (tipo_id);

create table d40_cliente (
    id                             number generated by default on null as identity 
                                   constraint d40_cliente_id_pk primary key,
    cliente                        varchar2(50 char)
                                   constraint d40_cliente_cliente_unq unique not null,
    zona                           varchar2(10 char) not null
)
;

create table d40_fornitore (
    id                             number generated by default on null as identity 
                                   constraint d40_fornitore_id_pk primary key,
    fornitore                      varchar2(50 char)
                                   constraint d40_fornitore_fornitore_unq unique not null,
    zona                           varchar2(10 char) not null
)
;

create table d40_acquisto (
    id                             number generated by default on null as identity 
                                   constraint d40_acquisto_id_pk primary key,
    fornitore_id                   number
                                   constraint d40_acquisto_fornitore_id_fk
                                   references d40_fornitore on delete cascade not null,
    prodotto_id                    number
                                   constraint d40_acquisto_prodotto_id_fk
                                   references d40_prodotto on delete cascade not null,
    data                           date not null,
    quantita                       number not null
)
;

-- table index
create index d40_acquisto_i1 on d40_acquisto (fornitore_id);
create index d40_acquisto_i2 on d40_acquisto (prodotto_id);

create table d40_vendita (
    id                             number generated by default on null as identity 
                                   constraint d40_vendita_id_pk primary key,
    cliente_id                     number
                                   constraint d40_vendita_cliente_id_fk
                                   references d40_cliente on delete cascade not null,
    prodotto_id                    number
                                   constraint d40_vendita_prodotto_id_fk
                                   references d40_prodotto on delete cascade not null,
    data                           date not null,
    quantita                       number not null
)
;

-- table index
create index d40_vendita_i1 on d40_vendita (cliente_id);
create index d40_vendita_i2 on d40_vendita (prodotto_id);

-- views
create or replace view d40_tipo_vw as
select tp.ID,
       tp.tipo,
       'https://raw.githubusercontent.com/robertocapancioni/APEX2021-3006-2807/main/d40/img/'||replace(replace(replace(lower(tp.tipo),' ','_'),'/',''),',','')||'.png' as tipo_logo
  from d40_tipo tp;       

create or replace view d40_prodotto_vw as 
select p.ID,
       p.PRODOTTO,
       p.PREZZO_ACQUISTO,
       p.PREZZO_VENDITA,
       tp.tipo,
       'https://raw.githubusercontent.com/robertocapancioni/APEX2021-3006-2807/main/d40/img/'||replace(replace(replace(lower(tp.tipo),' ','_'),'/',''),',','')||'.png' as tipo_logo,
       tp.gruppo 
  from d40_prodotto p
  join d40_tipo tp
    on p.tipo_id = tp.id;

create or replace view d40_acquisto_vw as
select a.id acquisto_id,
       a.data,
       to_char(a.data,'YYYY')anno,
       to_char(a.data,'YYYY-MM')annomese,
       to_char(a.data,'YYYY-Q')annotrimestre,
       to_char(a.data,'MM')mese,
       to_char(a.data,'Q')trimestre,
       p.prodotto,
       tp.tipo,
       'https://raw.githubusercontent.com/robertocapancioni/APEX2021-3006-2807/main/d40/img/'||replace(replace(replace(lower(tp.tipo),' ','_'),'/',''),',','')||'.png' as tipo_logo,
       tp.gruppo,
       f.fornitore,
       f.zona,
       a.quantita,
       a.quantita*p.prezzo_acquisto as importo
  from d40_acquisto a 
  join d40_prodotto p on a.prodotto_id = p.id
  join d40_tipo tp on p.tipo_id = tp.id
  join d40_fornitore f on a.fornitore_id = f.id;


  create or replace view d40_vendita_vw as
  select a.id vendita_id,
       a.data,
       to_char(a.data,'YYYY')anno,
       to_char(a.data,'YYYY-MM')annomese,
       to_char(a.data,'YYYY-Q')annotrimestre,
       to_char(a.data,'MM')mese,
       to_char(a.data,'Q')trimestre,
       p.prodotto,
       tp.tipo,
       'https://raw.githubusercontent.com/robertocapancioni/APEX2021-3006-2807/main/d40/img/'||replace(replace(replace(lower(tp.tipo),' ','_'),'/',''),',','')||'.png' as tipo_logo,
       tp.gruppo,
       c.cliente,
       c.zona,
       a.quantita,
       a.quantita*p.prezzo_vendita as importo
  from d40_vendita a 
  join d40_prodotto p on a.prodotto_id = p.id
  join d40_tipo tp on p.tipo_id = tp.id
  join d40_cliente c on a.cliente_id = c.id;

create or replace view d40_acquisto_anno_gruppo_vw as
select anno,
       gruppo,
       sum(quantita) as quantita, 
       sum(importo) as importo 
  from d40_acquisto_vw  
 group by 
       anno,
       gruppo 
 order by 
       anno, 
       gruppo;

